import React, { Component } from 'react';
import './App.css'
import Header from './components/Header';
import Timer from './components/Timer';


export default class App extends Component {
  constructor() {
    super()
    this.state ={
      showTimer: true,
    }
  }

  showToTimer() {
    const { showTimer } = this.state
    if(showTimer){
      return <Timer />
    }
      return null;
  }

  render() {
    return (
      <div>
        <Header />
        <Timer />
        <button>ESCONDER TIMER</button>
      </div>
    )
  }
}
